import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      monster: [
        {
          name: 'Frankenstein',
          id:'mon1'
        },
        {
          name: 'Dracula',
          id:'mon2'
        },
        {
          name: 'Zombie',
          id:'mon2'
        }
      ]
    }
  }
  render() {
    return (
      <div className="App">
        {
          this.state.monster.map(monster => <h1>{monster.name}</h1>)
        }
      </div>
    );
  }
}

export default App;
